import React from "react";
import { View, Text, Pressable } from "react-native";
import { createStackNavigator } from "@react-navigation/stack";
import { Ionicons } from "@expo/vector-icons";

const Stack = createStackNavigator();

const ChatListHeader = () => {
  return (
    <View
      style={{
        flexDirection: "row",
        justifyContent: "space-between",
        paddingHorizontal: 16,
        paddingVertical: 10,
      }}
    >
      {/* Tìm kiếm */}
      <Ionicons name="search" size={24} color="black" onPress={() => console.log("Search pressed")} />

      {/* QR */}
      <Ionicons name="qr-code" size={24} color="black" onPress={() => console.log("QR pressed")} />

      {/* Thêm bạn */}
      <Ionicons name="person-add" size={24} color="black" onPress={() => console.log("Add friend pressed")} />
    </View>
  );
};

const ChatList = ({ navigation }) => {
  return (
    <Stack.Navigator>
      <Stack.Screen
        name="ChatListScreen"
        component={() => (
          <View>
            {/* Header */}
            <ChatListHeader />

            {/* Nội dung trang */}
            <Text style={{ color: "red" }}>Chat List</Text>
            <Pressable
              style={{ borderRadius: 999, backgroundColor: "#00bcd4", padding: 10, marginTop: 10 }}
              onPress={() => navigation.navigate("ChatContent")}
            >
              <Text style={{ fontWeight: "bold", color: "red", textAlign: "center" }}>Chat</Text>
            </Pressable>
          </View>
        )}
      />
    </Stack.Navigator>
  );
};

export default ChatList;
