import { View, Text, Pressable, Image, StyleSheet, TextInput } from "react-native";
import React from "react";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";
import { ProgressBar } from "react-native-paper";

const Info = ({ navigation }) => {
  // navigation.setOptions({
  //   headerRight: () => (
  //     <View style={styles.headerRight}>
  //       <Pressable
  //         onPress={() => {
  //           navigation.navigate("PersonalSetting");
  //         }}
  //       >
  //         <Ionicons name="settings-outline" size={24} color="white" />
  //       </Pressable>
  //     </View>
  //   ),
  //   headerTitle: () => (
  //     <View style={styles.headerTitle}>
  //       <Ionicons
  //         name="search"
  //         size={24}
  //         color="white"
  //         style={{ position: "absolute", marginLeft: 10 }}
  //       />
  //       <Text style={styles.headerText}>Tìm kiếm</Text>
  //     </View>
  //   ),
  //   headerStyle: styles.headerStyle,
  //   headerTintColor: "#fff",
  //   headerTitleStyle: {
  //     fontWeight: "bold",
  //     fontSize: 20,
  //   },
  // });

  navigation.setOptions({
    headerRight: () => (
      <View style={styles.headerRightContainer}>
      
        <Pressable
          onPress={() => navigation.navigate("PersonalSetting")}
          style={styles.headerIcon}
        >
          <Ionicons name="settings-outline" size={24} color="white" />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View style={{ flexDirection: "row", alignItems: "center" }}>
        <Ionicons
          name="search"
          size={24}
          color="white"
          style={{ marginLeft: 5, marginRight: 20 }}
        />
        <TextInput
          onFocus={() => {
            navigation.navigate("SearchFriends");
          }}
          style={{
            height: 45,
            width: 300,
            marginLeft: 10,
          }}
          placeholder="Tìm kiếm"
          placeholderTextColor={"white"}
        />
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },
  });

  return (
    <View style={styles.container}>
      {/* View Info */}
      <View style={styles.infoContainer}>
        <Pressable
          onPress={() => {
            navigation.navigate("PersonalPage");
          }}
          style={styles.pressableContainer}
        >
          <View style={styles.avatarContainer}>
            <Image
              source={require("/assets/avata-story-3.png")}
              style={styles.avatar}
            ></Image>
          </View>
          <View style={styles.textContainer}>
            <Text style={styles.nameText}>Min Nguyên</Text>
            <Text style={styles.viewProfileText}>Xem trang cá nhân</Text>
          </View>
        </Pressable>
        <Pressable>
          <FontAwesomeIcons
            name="exchange-alt"
            size={24}
            color="#0091FF"
            style={{ marginHorizontal: 20, marginVertical: 20 }}
          />
        </Pressable>
      </View>

      {/* View select */}
      {/* Đăng ký nhạc chờ */}
      <Pressable style={styles.selectContainer}>
        <View style={styles.iconContainer}>
          <FontAwesomeIcons name="music" size={24} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <View style={styles.textRow}>
            <Text style={styles.boldText}>Nhạc chờ Zalo</Text>
          </View>
          <Text style={styles.grayText}>
            Đăng ký nhạc chờ, thể hiện cá tính
          </Text>
        </View>
        <View style={styles.iconContainer}>
          <FontAwesomeIcons name="crown" size={16} color="#e48e04" />
        </View>
      </Pressable>

      {/* Ví QR */}
      <Pressable style={styles.selectContainer}>
        <View style={styles.iconContainer}>
          <Ionicons name="qr-code-outline" size={24} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <Text style={styles.boldText}>Ví QR</Text>
          <Text style={styles.grayText}>
            Lưu trữ và xuất trình các mã QR quan trọng
          </Text>
        </View>
      </Pressable>

      {/* Clound */}
      <Pressable style={styles.selectContainer}>
        <View style={styles.iconContainer}>
          <Ionicons name="cloudy-outline" size={24} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <Text style={styles.boldText}>Clound của tôi</Text>
          <Text style={styles.grayText}>372,2 MB / 1 GB</Text>
          <ProgressBar style={{ height: 2 }} progress={0.5} color="#0091FF" />
        </View>
        <View style={styles.iconContainer}>
          <Ionicons name="arrow-forward" size={24} color="#cccccc" />
        </View>
      </Pressable>

      {/* Bộ nhớ */}
      <Pressable style={styles.selectContainer}>
        <View style={styles.iconContainer}>
          <FontAwesomeIcons name="chart-pie" size={24} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <Text style={styles.boldText}>Dung lượng và dữ liệu</Text>
          <Text style={styles.grayText}>Quản lý dữ liệu Zalo của bạn</Text>
        </View>
        <View style={styles.iconContainer}>
          <Ionicons name="arrow-forward" size={24} color="#cccccc" />
        </View>
      </Pressable>

      {/* Tài khoản và bảo mật*/}
      <Pressable
        onPress={() => {
          navigation.navigate("AccountVsSecurity");
        }}
        style={styles.selectContainer}
      >
        <View style={styles.iconContainer}>
          <Ionicons name="shield-checkmark" size={22} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <Text style={styles.boldText}>Tài khoản và bảo mật</Text>
        </View>
        <View style={styles.iconContainer}>
          <Ionicons name="arrow-forward" size={24} color="#cccccc" />
        </View>
      </Pressable>

      {/* Quyền riêng tư*/}
      <Pressable
        onPress={() => {
          navigation.navigate("PersonalPrivacy");
        }}
        style={styles.selectContainer}
      >
        <View style={styles.iconContainer}>
          <FontAwesomeIcons name="user-lock" size={22} color="#0091FF" />
        </View>
        <View style={styles.textContent}>
          <Text style={styles.boldText}>Quyền riêng tư</Text>
        </View>
        <View style={styles.iconContainer}>
          <Ionicons name="arrow-forward" size={24} color="#cccccc" />
        </View>
      </Pressable>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  headerRightContainer: {
    flexDirection: "row",
  },
  headerIcon: {
    padding: 20,
    marginLeft: 15,
  },
  headerTitleContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  searchIcon: {
    position: "absolute",
    marginLeft: 10,
  },
  headerTitleText: {
    color: "gray",
    fontSize: 18,
    marginLeft: 40,
  },
  infoContainer: {
    flexDirection: "row",
    backgroundColor: "white",
    marginBottom: 2,
    paddingHorizontal: 10,
  },
  pressableContainer: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
  },
  avatarContainer: {
    width: "20%",
    alignItems: "center",
  },
  avatar: {
    width: 60,
    height: 60,
    borderRadius: 30,
  },
  textContainer: {
    width: "80%",
    marginLeft: 10,
  },
  nameText: {
    fontSize: 16,
    fontWeight: "medium",
  },
  viewProfileText: {
    color: "gray",
    fontWeight: "bold",
  },
  selectContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 10,
    paddingVertical: 20,
    paddingHorizontal: 2,
    borderBottomColor: "gray",
    borderBottomWidth: 0.5,
  },
  iconContainer: {
    width: "20%",
    height: "100%",
    alignItems: "center",
    justifyContent: "center",
  },
  textContent: {
    width: "60%",
    marginLeft: 10,
  },
  textRow: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
  boldText: {
    fontWeight: "bold",
    fontSize: 16,
  },
  grayText: {
    color: "gray",
    fontWeight: "medium",
  },
});

export default Info;
