import React, { useState } from 'react';
import { Text, View, FlatList, Image, TextInput } from 'react-native';
import ChatItem from './ChatItem';
import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';



function Chat() {

  const navigation = useNavigation();
  const handleIconPress = (item) => {
    // Chuyển đến màn hình tin nhắn với thông tin từ item
    navigation.navigate('Message', { ten: item.ten });
  };

  navigation.setOptions({

    headerRight: () => (
      <View style={{ flexDirection: 'row' }}>
        <Ionicons name="qr-code" size={24} color="white" style={{ padding: 5, paddingStart: 15 }} />
        <Ionicons name="add" size={24} color="white" style={{ padding: 5 }} />
      </View>
    ),
    headerTitle: () => (
      <View style={{flexDirection:'row', alignItems: 'center'}}>
        <Ionicons name="search" size={24} color="white" style={{ position: 'absolute', marginLeft: 10 }} />
        <TextInput onFocus={() => { navigation.navigate("TimKiem") }} style={{ flex: 1, height: 45,width:300 , paddingLeft: 40, borderRadius: 10, color: 'white' }} placeholder='Tìm kiếm' placeholderTextColor={'white'} />
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },

  })
  // Mảng dữ liệu mẫu
  const [users, setUsers] = useState([
    {
      ten: 'Nguyen Van A',
      url: 'https://randomuser.me/api/portraits/men/68.jpg',
      tinNhan: 'Hello',
      soTNChuaDoc: 6,
      thoiGian: 2,
    },
    {
      ten: 'Luong thi Tho',
      url: 'https://randomuser.me/api/portraits/men/70.jpg',
      tinNhan: 'Khoe khong',
      soTNChuaDoc: 0,
      thoiGian: 6
    },
    {
      ten: 'Nguyen Van Teo',
      url: 'https://randomuser.me/api/portraits/men/80.jpg',
      tinNhan: 'An com chua',
      soTNChuaDoc: 0,
      thoiGian: 0
    },
    {
      ten: 'Le Van Ty',
      url: 'https://randomuser.me/api/portraits/men/90.jpg',
      tinNhan: 'Dang lam gi the',
      soTNChuaDoc: 5,
      thoiGian: 8
    },
    {
      ten: 'Huynh Quoc Hao',
      url: 'https://randomuser.me/api/portraits/men/10.jpg',
      tinNhan: 'may gio roi',
      soTNChuaDoc: 7,
      thoiGian: 0
    },
    {
      ten: 'Bui Tri Thuc',
      url: 'https://randomuser.me/api/portraits/men/20.jpg',
      tinNhan: 'lam bai tap chua',
      soTNChuaDoc: 3,
      thoiGian: 12
    },
    {
      ten: 'Thanh Tam',
      url: 'https://randomuser.me/api/portraits/men/53.jpg',
      tinNhan: 'lam bai tap chua',
      soTNChuaDoc: 0,
      thoiGian: 5
    },
    {
      ten: 'Hung Dung',
      url: 'https://randomuser.me/api/portraits/men/62.jpg',
      tinNhan: 'lam bai tap chua',
      soTNChuaDoc: 4,
      thoiGian: 0
    },
    {
      ten: 'Nam',
      url: 'https://randomuser.me/api/portraits/men/26.jpg',
      tinNhan: 'lam bai tap chua',
      soTNChuaDoc: 7,
      thoiGian: 2
    },
    {
      ten: 'Hau',
      url: 'https://randomuser.me/api/portraits/men/63.jpg',
      tinNhan: 'lam bai tap chua',
      soTNChuaDoc: 0,
      thoiGian: 3
    },
  ]);
  return (
    <View style={{ flex: 1 }}>
      {/* FlatList component */}
      <FlatList
        data={users}
        renderItem={({ item }) => (
          <ChatItem
            item={item}
            onPress={() => handleIconPress(item)}
          />
        )}
        keyExtractor={item => item.url}
      />
    </View>
  );

}

export default Chat
