/* eslint-disable react/prop-types */
import { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export const useAuthContext = () => {
  return useContext(AuthContext);
};

export const AuthContextProvider = ({ children }) => {
  const [authUser, setAuthUser] = useState(localStorage.getItem('authUser'));
  const [accessToken, setAccessToken] = useState(localStorage.getItem('accessToken'));
  const [refreshToke, setrefreshToke] = useState(localStorage.getItem('refreshToke'));

  useEffect(() => {
     localStorage.setItem("authUser", JSON.stringify(authUser));
     localStorage.setItem("accessToken", JSON.stringify(accessToken));
     localStorage.setItem("refreshToke", JSON.stringify(refreshToke));
  }, [authUser, accessToken]);

  return (
    <AuthContext.Provider
      value={{ authUser, setAuthUser, accessToken, setAccessToken, refreshToke, setrefreshToke }}
    >
      {children}
    </AuthContext.Provider>
  );
};
