const News = ({ navigation }) => {
  navigation.setOptions({
    headerRight: () => (
      <View style={styles.headerRightContainer}>
        <Pressable style={styles.headerIcon}>
          <Ionicons name="image" size={24} color="white" />
        </Pressable>
        <Pressable
          onPress={() => navigation.navigate("Notice")}
          style={styles.headerIcon}
        >
          <FontAwesomeIcons name="bell" size={24} color="white" />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View style={styles.headerTitleContainer}>
        <Ionicons
          name="search"
          size={24}
          color="white"
          style={styles.searchIcon}
        />
        <Text style={styles.headerTitleText}>Tìm kiếm</Text>
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },
  });

  return (
    <View>
      {/* Đăng status */}
      <View style={styles.statusContainer}>
        <View style={styles.avatarContainer}>
          <Image
            source={require("/assets/avata-story-3.png")}
            style={styles.avatar}
          />
        </View>
        <Pressable
          style={styles.statusTextContainer}
          onPress={() => {
            navigation.navigate("PostStatus");
          }}
        >
          <Text style={styles.statusText}>Hôm nay bạn thế nào?</Text>
        </Pressable>
      </View>

      {/* Button: ảnh, video, albul, kỉ niệm */}
      <View style={styles.buttonContainer}>
        <Pressable style={styles.button}>
          <Ionicons name="image" size={30} color="#56ce83" />
          <Text style={styles.buttonText}>Ảnh</Text>
        </Pressable>
        <Pressable style={styles.button}>
          <Ionicons name="videocam" size={30} color="#df34be" />
          <Text style={styles.buttonText}>Video</Text>
        </Pressable>
        <Pressable style={styles.button}>
          <Ionicons name="albums" size={30} color="#0a6bf4" />
          <Text style={styles.buttonText}>Album</Text>
        </Pressable>
        <Pressable style={styles.button}>
          <FontAwesomeIcons name="clock" size={30} color="#e67627" />
          <Text style={styles.buttonText}>Kỷ niệm</Text>
        </Pressable>
      </View>

      {/* List story */}
      {/* Your story list JSX code here */}

      {/* List status */}
      {/* Your status list JSX code here */}
    </View>
  );
};

const styles = StyleSheet.create({
  headerRightContainer: {
    flexDirection: "row",
  },
  headerIcon: {
    padding: 5,
    marginLeft: 15,
  },
  headerTitleContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  searchIcon: {
    position: "absolute",
    marginLeft: 10,
  },
  headerTitleText: {
    color: "#71b9fe",
    fontSize: 18,
    fontWeight: "bold",
    marginLeft: 24,
  },
  statusContainer: {
    flexDirection: "row",
    backgroundColor: "white",
    padding: 10,
    alignItems: "center",
  },
  avatarContainer: {
    width: "20%",
  },
  avatar: {
    width: 64,
    height: 64,
  },
  statusTextContainer: {
    width: "80%",
  },
  statusText: {
    color: "#545454",
    fontSize: 18,
  },
  buttonContainer: {
    flexDirection: "row",
    backgroundColor: "white",
    padding: 10,
    justifyContent: "space-between",
  },
  button: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#D9D9D9",
    borderRadius: 10,
    width: "22%",
    justifyContent: "center",
    padding: 8,
  },
  buttonText: {
    fontWeight: "bold",
    marginLeft: 5,
  },
});

export default News;
