import { View, Text, Image, Pressable } from 'react-native'
import React, { useState } from 'react'
import { Ionicons } from '@expo/vector-icons';
import { Switch } from 'react-native';
const TuyChonBanBe = () => {
    const [isĐanDauBanThan, setĐanhauBanThan] = useState(false);
    const danhDauBanThan = () => setĐanhauBanThan(previousState => !previousState)
    return (
        <View style={{ flex: 1 }}>
            <Pressable style={{ height: 50,backgroundColor: 'white', marginStart: 22, justifyContent:'center'}}>
                <Text style={{ fontSize: 18 }}>Thông tin</Text>
            </Pressable>
            <View style={{height: 1, backgroundColor:'gray',marginStart:20,marginEnd:10, opacity:0.2}}/>
            <Pressable style={{ height: 50,backgroundColor: 'white', marginStart: 22, justifyContent:'center'}}>
                <Text style={{ fontSize: 18 }}>Đổi tên gợi nhớ</Text>
            </Pressable>
            <View style={{height: 1, backgroundColor:'gray',marginStart:20,marginEnd:10, opacity:0.2}}/>
            <Pressable style={{ height: 50,backgroundColor: 'white', marginStart: 22, justifyContent:'center'}}>
                <View style={{flexDirection:'row'}}>
                <Text style={{ fontSize: 18 }}>Đánh dấu bạn thân</Text>
                <Switch
                    trackColor={{ false: '#767577', true: '#0091FF' }}
                    thumbColor={isĐanDauBanThan ? '#0091FF' : '#f4f3f4'}
                    onValueChange={danhDauBanThan}
                    value={isĐanDauBanThan}
                    style={{marginEnd :15}}
                />
                </View>
            </Pressable>
            <View style={{height: 1, backgroundColor:'gray',marginStart:20,marginEnd:10, opacity:0.2}}/>
            <Pressable style={{ height: 50,backgroundColor: 'white', marginStart: 22, justifyContent:'center'}}>
                <Text style={{ fontSize: 18 }}>Giới thiệu cho bạn bè</Text>
                
            </Pressable>
      </View>
    )
}

export default TuyChonBanBe
