import React, { useState, useEffect } from 'react';
import { View, Text, Pressable, Image, TextInput, Modal } from "react-native";
import { Ionicons } from "@expo/vector-icons";



const GroupDirectory = ({ navigation }) => {
    const [friend, setFriend] = useState('');

    return (
        <View className={"flex-1 bg-slate-200"}>
            <View className={"flex flex-col bg-white"}>
                <Pressable className={"flex flex-row m-2"}> <Image style={{ width: null, height: '40px', aspectRatio: 1 }} resizeMode='contain' source={require('/assets/createGroup.png')} ></Image>
                    <Text className={"flex justify-center items-center text-base font-semibold ml-2"}>Tạo nhóm mới</Text>
                </Pressable>
            </View>
            <View className={"flex flex-col bg-white mt-2"}>
                <Text className={"flex justify-center items-center text-base font-semibold ml-2"}>Tính năng nổi bật</Text>
                <View className={"flex flex-row "}>

                    <Pressable className={"flex flex-row m-2"}><Ionicons name={"people-circle-sharp"} size={40} color={"#0091FF"} />
                        <Text className={"flex justify-center items-center text-base font-semibold ml-2"}>Lời mời kết bạn</Text>
                    </Pressable>

                </View>
            </View>

        </View>
    )
};
export default GroupDirectory;
