import React, { useState } from "react";
import { View, Text, Pressable, Image, TextInput } from "react-native";


const CreateAccount = ({ navigation }) => {
    const [textName, setTextName] = useState();
    const [isValidName, setIsValidName] = useState(false);

    const handleTextChange = (input) => {
        // Kiểm tra nếu input chỉ chứa chữ cái và có độ dài từ 2 đến 40 ký tự
        const isValidInput = /^[a-zA-Z]{2,40}$/.test(input);

        // Nếu là input hợp lệ, cập nhật giá trị textName
        if (isValidInput || input === "") {
            setTextName(input);
            setIsValidName(true);
        } else {
            setTextName(input);
            setIsValidName(false);
        }
    };
    const handlePressablePress = () => {
        // Xử lý khi Pressable được bấm
        if (isValidName) {
            navigation.navigate("CreateAccount")
        }
    };

    return (
        <View className={"flex-1  bg-white"}>
            <View className={"m-5"}>
                <Text className={"font-bold text-black-400 text-xl"}>Tên zalo</Text>
                <TextInput
                    onChangeText={handleTextChange}
                    value={textName}
                    style={{ borderBottomColor: '#64D6EA', borderBottomWidth: 2 }} placeholder="Gồm 2-40 kí tự" className={"font-semibold placeholder-gray-400 text-lg focus:outline-none mt-5"}></TextInput>
                <Text className={"font-semibold text-black-400 mt-5"}>Lưu ý khi đặt tên:</Text>
                <Text className={"font-semibold text-black-400 mt-5"}>Không vi phạm <Pressable><Text className={"font-semibold text-blue-500 p-2"}>Quy định đặt tên trên Zalo</Text></Pressable></Text>
                <Text className={"font-semibold text-black-400 "}>Nên sử dụng tên thật để giúp bạn bè dễ nhận ra bạn</Text>
            </View>
            <View className={"flex-1 justify-end items-end m-5"}>
                <Pressable style={{ width: 70, height: 70, justifyContent: "center", alignItems: "center", backgroundColor: isValidName ? '#0091FF' : '#BFD3F8', }} className={"btn rounded-full font-bold text-white"}
                disabled={!isValidName} onPress={handlePressablePress}>
                    <View><Image style={{ width: 50, height: 50 }} source={require('/assets/arrow.png')} ></Image>
                    </View></Pressable>
            </View>

        </View>
    )
};
export default CreateAccount;
