import React, { useState } from 'react';
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Ionicons } from "@expo/vector-icons";
import { View, Text, Pressable, Image, TextInput, } from "react-native";
import { CheckBox } from 'react-native-elements';
import CountryDropdown from './CountryDropdown'


const CreateAccount1 = () => {
    const [isCheckedUse, setIsCheckedUse] = useState(false);
    const [isCheckedInter, setIsCheckedInter] = useState(false);
    const handleCheckUse = () => {
        setIsCheckedUse(!isCheckedUse);
    };
    const handleCheckInter = () => {
        setIsCheckedInter(!isCheckedInter);
    };

    return (
        <View className={"flex-1  bg-white"}>
            <View className={"bg-gray-200"}><Text className={"font-bold text-black-400 p-2 text-center"}>Nhập số điện thoại của bạn để tạo tài khoản mới</Text></View>
            <View className={"flex flex-row m-5"} style={{ borderBottomColor: '#64D6EA', borderBottomWidth: 2 }}>
                <CountryDropdown />
                <TextInput placeholder="Gồm 2-40 kí tự" className={"pl-3 font-semibold placeholder-gray-400 text-lg focus:outline-none "}></TextInput>

            </View>
            <View className={"flex flex-col"}>
                <CheckBox
                    checked={isCheckedUse}
                    onPress={handleCheckUse}
                    title={
                        <View className={'flex-row'}>
                            <Text className={"font-medium text-black "}>
                                Tôi đồng ý với các <Text className={"font-medium text-sky-400 "}>
                                    điều khoản sử dụng Zalo
                                </Text></Text>
                        </View>
                    }
                />
                <CheckBox
                    checked={isCheckedInter}
                    onPress={handleCheckInter}
                    title={
                        <View className={'flex-row'}>
                            <Text className={"font-medium text-black "}>
                                Tôi đồng ý với các <Text className={"font-medium text-sky-400 "}>
                                    điều khoản Mạng xã hội của Zalo
                                </Text></Text>
                        </View>
                    }
                />

            </View>
            <View className={"flex-1 justify-end items-end m-5"}>
                <Pressable style={{ width: 70, height: 70, justifyContent: "center", alignItems: "center" }} className={"btn rounded-full bg-blue-400 font-bold text-white"}
                >
                    <View><Image style={{ width: 50, height: 50 }} source={require('/assets/arrow.png')} ></Image>
                    </View></Pressable>
            </View>





            <View style={styles.container}>
                <TouchableOpacity onPress={toggleModal}>
                    <Text style={styles.openButton}>Open Modal</Text>
                </TouchableOpacity>

                <Modal
                    animationType="slide"
                    transparent={true}
                    visible={isModalVisible}
                    onRequestClose={toggleModal}
                >
                    <View style={styles.modalContainer}>
                        <View style={styles.modalContent}>
                            <Text>Your modal content goes here.</Text>
                            <TouchableOpacity onPress={toggleModal}>
                                <Text style={styles.closeButton}>Close Modal</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </Modal>
            </View>



        </View>
    )
};
export default CreateAccount1;
