import React, { useState, useEffect } from "react";
import { View, Text, Pressable, FlatList } from "react-native";

const ChatList = ({ navigation }) => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Simulate fetching messages from an API
    // Replace this with your actual data fetching logic
    const fetchData = async () => {
      // Fetch your messages here
      // For example:
      const response = await fetch('https://your-api-endpoint/messages');
      const data = await response.json();
      setMessages(data);
    };

    fetchData();
  }, []);

  const renderItem = ({ item }) => (
    <Pressable
      onPress={() => navigation.navigate("ChatContent", { messageId: item.id })}
      style={({ pressed }) => [
        {
          backgroundColor: pressed ? "rgb(210, 230, 255)" : "white",
        },
        styles.messageItem,
      ]}
    >
      <Text style={styles.messageText}>{item.text}</Text>
    </Pressable>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Chat List</Text>
      <FlatList
        data={messages}
        renderItem={renderItem}
        keyExtractor={(item) => item.id.toString()}
      />
    </View>
  );
};

const styles = {
  container: {
    flex: 1,
    padding: 16,
  },
  header: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 16,
  },
  messageItem: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: "#ddd",
  },
  messageText: {
    fontSize: 16,
  },
};

export default ChatList;
