import React, { useState } from "react";
import {
  View,
  Text,
  Pressable,
  Image,
  TextInput,
  StyleSheet,
} from "react-native";

const CreateAccount = ({ navigation }) => {
  const [textName, setTextName] = useState("");
  const [isValidName, setIsValidName] = useState(false);

  const handleTextChange = (input) => {
    const isValidInput = /^([a-zA-Zá-ỹÁ-Ỹ\s]{2,40})$/.test(input);

    setTextName(input);
    if (isValidInput) {
      setIsValidName(true);
    } else {
      setIsValidName(false);
    }
  };
  const handlePressablePress = () => {
    if (isValidName) {
      navigation.navigate("CreateAccount1");
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.inputContainer}>
        <Text style={styles.label}>Tên zalo</Text>
        <TextInput
          onChangeText={handleTextChange}
          value={textName}
          placeholder="Gồm 2-40 kí tự"
          style={styles.input}
        ></TextInput>
        <Text style={styles.note}>Lưu ý khi đặt tên:</Text>
        <Text style={styles.note}>
          Không vi phạm Quy định đặt tên trên Zalo
        </Text>
        <Text style={styles.note}>
          Nên sử dụng tên thật để giúp bạn bè dễ nhận ra bạn
        </Text>
      </View>
      <View style={styles.buttonContainer}>
        <Pressable
          style={[
            styles.button,
            isValidName ? styles.validButton : styles.invalidButton,
          ]}
          disabled={!isValidName}
          onPress={handlePressablePress}
        >
          <View>
            <Image
              style={styles.buttonIcon}
              source={require("../../../assets/arrow.png")}
            ></Image>
          </View>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  inputContainer: {
    margin: 5,
  },
  label: {
    fontWeight: "bold",
    fontSize: 20,
    // color: "#000",
  },
  input: {
    fontWeight: "bold",
    fontSize: 16,
    borderColor: "#64D6EA",
    borderBottomWidth: 2,
    marginBottom: 20,
  },
  note: {
    fontWeight: "bold",
    fontSize: 16,
    color: "#000",
    marginBottom: 20,
  },
  buttonContainer: {
    flex: 1,
    justifyContent: "flex-end",
    alignItems: "flex-end",
    margin: 5,
  },
  button: {
    width: 70,
    height: 70,
    borderRadius: 35,
    justifyContent: "center",
    alignItems: "center",
  },
  validButton: {
    backgroundColor: "#0091FF",
  },
  invalidButton: {
    backgroundColor: "#BFD3F8",
  },
  buttonIcon: {
    width: 50,
    height: 50,
  },
});

export default CreateAccount;
