import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Ionicons } from "@expo/vector-icons";
import { View, Text, Pressable, Image, TextInput } from "react-native";


const CreateAccount = () => {
    return (
        <View className={"flex-1 flex-col bg-white m-5"}>
            <Text className={"font-bold text-black-400 p-2"}>Tên zalo</Text>
            <TextInput style={{borderBottomColor:'#64D6EA', borderBottomWidth:2}} placeholder="Gồm 2-40 kí tự"  className={"font-semibold text-gray-400 p-2"}></TextInput>
            <View>
                <Text className={"font-semibold text-black-400 p-2"}>Lưu ý khi đặt tên:</Text>
                <Text className={"font-semibold text-black-400 p-2"}>Không vi phạm <Pressable><Text className={"font-semibold text-blue-500 p-2"}>Quy định đặt tên trên Zalo</Text></Pressable></Text>
                <Text className={"font-semibold text-black-400 p-2"}>Nên sử dụng tên thật để giúp bạn bè dễ nhận ra bạn</Text>
            </View>
        </View>
    )
};
export default CreateAccount;
