import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Ionicons } from "@expo/vector-icons";
import { View, Text, Pressable, Image, TextInput } from "react-native";


const CreateAccount = () => {
    return (
        <View className={"flex-1 flex-col bg-white"}>
            <View className={"m-5"}>
                <Text className={"font-bold text-black-400"}>Tên zalo</Text>
                <TextInput style={{ borderBottomColor: '#64D6EA', borderBottomWidth: 2 }} placeholder="Gồm 2-40 kí tự" className={"font-semibold placeholder-gray-400  focus:outline-none"}></TextInput>

                <Text className={"font-semibold text-black-400"}>Lưu ý khi đặt tên:</Text>
                <Text className={"font-semibold text-black-400 "}>Không vi phạm <Pressable><Text className={"font-semibold text-blue-500 p-2"}>Quy định đặt tên trên Zalo</Text></Pressable></Text>
                <Text className={"font-semibold text-black-400 "}>Nên sử dụng tên thật để giúp bạn bè dễ nhận ra bạn</Text>
            </View>
            <View>
                <Pressable className={"btn rounded-full w-10 bg-blue-500 font-bold text-white"}>
                    <View><Image style={{ width: 50, height: 50, aspectRatio: 1 }} resizeMode='contain' source={require('/assets/arrow.png')} ></Image>
                    </View></Pressable>
            </View>
        </View>
    )
};
export default CreateAccount;
