import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Ionicons } from "@expo/vector-icons";
import { View, Text, TextInput } from "react-native";


import ChatList from "./ChatList";
import Contact from "./Contact";
import Explore from "./Explore";
import News from "./News";
import Info from "./Info";

const Tab = createBottomTabNavigator();

const ChatComponent = () => {
  const handleTabPress = (route, navigation) => {
    // Xử lý logic khi tab được nhấn
    if (route.name === "ChatList") {
      console.log("ChatList tab pressed");
      // Thực hiện các thao tác mong muốn khi tab ChatList được nhấn
    } else if (route.name === "Contact") {
      console.log("Contact tab pressed");
      // Thực hiện các thao tác mong muốn khi tab Contact được nhấn
    } else if (route.name === "Explore") {
      console.log("Explore tab pressed");
      // Thực hiện các thao tác mong muốn khi tab Explore được nhấn
    } else if (route.name === "News") {
      console.log("News tab pressed");
      // Thực hiện các thao tác mong muốn khi tab News được nhấn
    } else if (route.name === "Info") {
      console.log("Info tab pressed");
      // Thực hiện các thao tác mong muốn khi tab Info được nhấn
    }
  };

  return (
    <Tab.Navigator
      initialRouteName="ChatList"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === "ChatList") {
            iconName = focused
              ? "chatbubble-ellipses"
              : "chatbubble-ellipses-outline";
          } else if (route.name === "Contact") {
            iconName = focused ? "people" : "people-outline";
          } else if (route.name === "Explore") {
            iconName = focused ? "compass" : "compass-outline";
          } else if (route.name === "News") {
            iconName = focused ? "newspaper" : "newspaper-outline";
          } else if (route.name === "Info") {
            iconName = focused
              ? "information-circle"
              : "information-circle-outline";
          }
          return <Ionicons name={iconName} size={size} color={color} />;
        },
        headerTitle: () => (
          <View style={{ flex: 1, flexDirection: "row", alignItems: "center" }}>
             <Ionicons name="search" size={24} color="white" onPress={() => console.log("Search pressed")} />
            <TextInput placeholder="Tìm kiếm" style={{ flex: 1, color: "white" , marginLeft:20}} />
          </View>
        ),
        headerRight: () => (
          <View style={{ flexDirection: "row", marginRight:10 , justifyContent: 'center', alignItems: 'center'}}>
            <Ionicons name="qr-code" size={24} color="white" onPress={() => console.log("QR pressed")} />
             <View style={{marginLeft:10}}>
             <Ionicons name="add" size={35} color="white" onPress={() => console.log("Add friend pressed")} />
             </View>
          </View>
        ),
        headerStyle: {
          backgroundColor: "#01A4F6", // Màu nền của header
        },
        headerTintColor: "white", // Màu chữ của header
      })}
      tabBarOptions={{
        activeTintColor: 'tomato',
        inactiveTintColor: 'gray',
      }}
      screenOptions={({ route, navigation }) => ({
        tabBarPress: (e) => handleTabPress(route, navigation),
      })}
    >
      <Tab.Screen name="ChatList" component={ChatList} />
      <Tab.Screen name="Contact" component={Contact} />
      <Tab.Screen name="Explore" component={Explore} />
      <Tab.Screen name="News" component={News} />
      <Tab.Screen name="Info" component={Info} />
    </Tab.Navigator>
  );
};

export default ChatComponent;
