import React, { useState } from "react";
import { View, Text, Image, Pressable, TextInput } from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";

const PersonalPage = ({ navigation }) => {
  const [status, setStatus] = useState("");

  navigation.setOptions({
    headerRight: () => (
      <View style={{ flexDirection: "row", paddingHorizontal: 16 }}>
        <Pressable style={{ paddingHorizontal: 8 }}>
          <Ionicons name="sync-circle-outline" size={24} color="white" />
        </Pressable>
        <Pressable
          style={{ paddingHorizontal: 8 }}
          onPress={() => {
            navigation.navigate("PersonalDetail");
          }}
        >
          <Ionicons name="ellipsis-horizontal-sharp" size={24} color="white" />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View>
        <Text
          style={{ fontWeight: "bold", fontSize: 20, color: "#fff" }}
        ></Text>
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },
  });

  return (
    <View style={{ backgroundColor: "#f1f2f6" }}>
      <Pressable
        style={{
          width: "100%",
          justifyContent: "center",
          alignItems: "center",
        }}
      >
        <Image
          source={require("/assets/cover-image.png")}
          style={{ width: "100%", height: 176 }}
        />
        <Image
          source={require("/assets/avata-story-3.png")}
          style={{ width: 96, height: 96 }}
        />
      </Pressable>
      <View style={{ width: "100%", alignItems: "center" }}>
        <Text style={{ fontWeight: "bold", fontSize: 24, marginTop: 8 }}>
          Min Nguyên
        </Text>
        <View style={{ flexDirection: "row", marginVertical: 8 }}>
          <FontAwesomeIcons name="pen" size={18} color="#66a1f0" />
          <Text
            style={{ color: "#66a1f0", fontWeight: "bold", paddingLeft: 4 }}
          >
            Cập nhật giới thiệu bản thân
          </Text>
        </View>
      </View>
      <View
        style={{
          flexDirection: "row",
          width: "100%",
          justifyContent: "space-evenly",
          marginTop: 16,
        }}
      >
        <Pressable
          style={{
            backgroundColor: "white",
            flexDirection: "row",
            padding: 4,
            width: 144,
            height: 48,
            justifyContent: "space-between",
            alignItems: "center",
            borderRadius: 8,
          }}
        >
          <Ionicons name="image" size={26} color="#006af5" />
          <Text style={{ fontWeight: "bold" }}>Ảnh của tôi</Text>
          <Text style={{ color: "#808080" }}>2,3K</Text>
        </Pressable>
        <Pressable
          style={{
            backgroundColor: "white",
            flexDirection: "row",
            padding: 4,
            width: 180,
            height: 48,
            justifyContent: "space-between",
            alignItems: "center",
            borderRadius: 8,
          }}
        >
          <FontAwesomeIcons name="shopping-bag" size={26} color="#12aee3" />
          <Text style={{ fontWeight: "bold", paddingLeft: 8 }}>
            Kho khoảnh khắc
          </Text>
          <Text style={{ color: "#808080" }}>11</Text>
        </Pressable>
      </View>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          width: "80%",
          alignSelf: "center",
          marginTop: 16,
        }}
      >
        <TextInput
          value={status}
          onChangeText={setStatus}
          style={{ backgroundColor: "white", color: "red", width: "80%" }}
          placeholder="Bạn đang nghĩ gì"
        />
        <Pressable
          style={{
            backgroundColor: "white",
            height: 48,
            width: "20%",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Ionicons name="image" size={26} color="#a4ce50" />
        </Pressable>
      </View>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          width: "80%",
          marginVertical: 8,
          alignSelf: "center",
        }}
      >
        <Pressable
          style={{
            backgroundColor: "#d4dce2",
            width: 32,
            height: 32,
            justifyContent: "center",
            alignItems: "center",
            borderRadius: 16,
          }}
        >
          <Ionicons name="lock-closed" size={16} color="#899097" />
        </Pressable>
        <View style={{ width: "90%", paddingHorizontal: 16 }}>
          <Text style={{ color: "#808080", fontWeight: "bold" }}>
            Bạn bè của bạn sẽ không xem được các bài đăng dưới đây.{" "}
            <Pressable>
              <Text style={{ color: "#12aee3", fontWeight: "bold" }}>
                Thay đổi cài đặt
              </Text>
            </Pressable>
          </Text>
        </View>
      </View>
      {/* Remaining JSX */}
    </View>
  );
};

