import React from 'react';
import { View, Text, TouchableOpacity, TextInput } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const Message = ({navigation}) => {

  navigation.setOptions({

    headerRight: () => (
      <View style={{ flexDirection: 'row' }}>
       <Pressable>
       <Ionicons name="qr-code" size={24} color="white" style={{ padding: 5, paddingStart: 15 }} />
       </Pressable>
        <Ionicons name="add" size={24} color="white" style={{ padding: 5 }} />
      </View>
    ),
    headerTitle: () => (
      <View style={{flexDirection:'row', alignItems: 'center'}}>
        <Ionicons name="search" size={24} color="white" style={{ position: 'absolute', marginLeft: 10 }} />
        <TextInput onFocus={() => { navigation.navigate("TimKiem") }} style={{ flex: 1, height: 45,width:300 , paddingLeft: 40, borderRadius: 10, color: 'white' }} placeholder='Tìm kiếm' placeholderTextColor={'white'} />
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },

  })

  return (
    <View style={{flex: 1, backgroundColor: '#E5E9EB'}}>
      {/* Nội dung màn hình */}
      <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
        <Text>Màn hình Message</Text>
      </View>

      {/* Phần dưới màn hình */}
      <View style={{flexDirection: 'row', justifyContent: 'space-around', alignItems: 'center', padding: 10, backgroundColor:'white'}}>
        {/* Icon mặt cười */}
        <TouchableOpacity onPress={() => console.log('Pressed smiley')}>
          <Ionicons name="happy-outline" size={30} color="black" />
        </TouchableOpacity>

        {/* Ô nhập tin nhắn */}
        <TextInput
          style={{flex: 1, height: 40, borderColor: 'gray', marginLeft: 10, marginRight: 10, borderRadius: 5, paddingLeft: 10}}
          placeholder="Tin nhắn"
        />

        {/* Icon menu */}
        <TouchableOpacity onPress={() => console.log('Pressed menu')}>
          <Ionicons name="ellipsis-horizontal-outline" size={30} color="black" style={{marginRight:10}}/>
        </TouchableOpacity>

        {/* Icon micro */}
        <TouchableOpacity onPress={() => console.log('Pressed microphone')}>
          <Ionicons name="mic-outline" size={30} color="black" style={{marginRight:10}}/>
        </TouchableOpacity>

        {/* Icon hình ảnh */}
        <TouchableOpacity onPress={() => console.log('Pressed image')}>
          <Ionicons name="image-outline" size={30} color="black" style={{}} />
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default Message;
