import React from "react";
import {
  View,
  Text,
  ImageBackground,
  Pressable,
  Image,
  StyleSheet,
} from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";

const Notice = ({ navigation }) => {
  navigation.setOptions({
    headerRight: () => (
      <View style={styles.headerRightContainer}>
        <Pressable>
          <Ionicons
            name="settings-outline"
            size={24}
            color="white"
            style={styles.headerRightIcon}
          />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View style={styles.headerTitleContainer}>
        <Text style={styles.headerTitleText}>Thông báo mới</Text>
      </View>
    ),
    headerStyle: styles.headerStyle,
    headerTintColor: styles.headerTintColor,
    headerTitleStyle: styles.headerTitleStyle,
  });

  return (
    <View style={styles.container}>
      {/* Notice đăng story*/}
      <View style={styles.noticeContainer}>
        <Pressable style={styles.noticeAvatarContainer}>
          <ImageBackground
            source={require("/assets/avata-story-1.png")}
            style={styles.noticeAvatarBackground}
            resizeMode="contain"
          >
            <View style={styles.noticeIconContainer}>
              <FontAwesomeIcons name="video" size={14} color="white" />
            </View>
          </ImageBackground>
        </Pressable>
        <View style={styles.noticeContentContainer}>
          <Text style={styles.noticeContentText}>
            Hoàng Thư{" "}
            <Text style={styles.noticeContentTextMedium}>
              đăng khoảng khắc mới{" "}
            </Text>
          </Text>
          <Text style={styles.noticeTimeText}>18:40 hôm qua</Text>
        </View>
        <Pressable style={styles.noticeOptionsContainer}>
          <Image
            source={require("/assets/ic_threeDots.png")}
            style={styles.noticeOptionsIcon}
            resizeMode="contain"
          />
        </Pressable>
      </View>

      {/* Notice đăng ảnh */}
      <View style={styles.noticeContainer}>
        {/* Tiếp tục với các phần tử khác */}
      </View>

      {/* Notice bình luận ảnh */}
      <View style={styles.noticeContainer}>
        {/* Tiếp tục với các phần tử khác */}
      </View>

      {/* Notice bày tỏ cảm xúc tym */}
      <View style={styles.noticeContainer}>
        {/* Tiếp tục với các phần tử khác */}
      </View>
    </View>
  );
};

export default Notice;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "white",
  },
  headerRightContainer: {
    flexDirection: "row",
  },
  headerRightIcon: {
    padding: 5,
    paddingStart: 15,
  },
  headerTitleContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  headerTitleText: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
  },
  headerStyle: {
    backgroundColor: "#0091FF",
    shadowColor: "#fff",
  },
  headerTintColor: "#fff",
  headerTitleStyle: {
    fontWeight: "bold",
    fontSize: 20,
  },
  noticeContainer: {
    flexDirection: "row",
    alignItems: "center",
    height: 96,
    backgroundColor: "rgb(226,243,251)",
    width: "100%",
  },
  noticeAvatarContainer: {
    paddingHorizontal: 12,
    width: "20%",
    height: "80%",
  },
  noticeAvatarBackground: {
    width: "100%",
    height: "100%",
    justifyContent: "flex-end",
    alignItems: "flex-end",
  },
  noticeIconContainer: {
    backgroundColor: "#9570d9",
    borderWidth: 2,
    borderColor: "white",
    borderRadius: 50,
    alignItems: "center",
    justifyContent: "center",
    width: 32,
    height: 32,
  },
  noticeContentContainer: {
