import { View, Text, Pressable, Image } from "react-native";
import React, { useState } from "react";

const ChatList = ({navigation}) => {
  const [users, setUsers] = useState([
    {
      ten: 'Nguyen Van A',
      url: 'https://randomuser.me/api/portraits/men/68.jpg',
      tinNhan:'Hello',
      soTNChuaDoc: 6,
    },
    {
      ten: 'Luong thi Tho',
      url: 'https://randomuser.me/api/portraits/men/70.jpg',
      tinNhan:'Khoe khong',
      soTNChuaDoc: 0,
    },
    {
      ten: 'Nguyen Van Teo',
      url: 'https://randomuser.me/api/portraits/men/80.jpg',
      tinNhan:'An com chua',
      soTNChuaDoc: 0
    },
    {
      ten: 'Le Van Ty',
      url: 'https://randomuser.me/api/portraits/men/90.jpg',
      tinNhan:'Dang lam gi the',
      soTNChuaDoc: 5
    },
    {
      ten: 'Huynh Quoc Hao',
      url: 'https://randomuser.me/api/portraits/men/10.jpg',
      tinNhan:'may gio roi',
      soTNChuaDoc: 7
    },
    {
      ten: 'Bui Tri Thuc',
      url: 'https://randomuser.me/api/portraits/men/20.jpg',
      tinNhan:'lam bai tap chua',
      soTNChuaDoc: 3
    },
  ])
  return (
    <View style={{flex: 1}}>
      <View>
        <View style={{height:75, backgroundColor:'purple', flexDirection: "row", alignItems:'center', marginHorizontal:15}}>
          <Image source={{uri: 'https://randomuser.me/api/portraits/men/20.jpg'}} style={{width:50,height:50, borderRadius:25, marginEnd:15}}/>
         <View>
         <Text style={{fontSize: 16}}>Huynh Quoc Hao</Text>
         <Text style={{fontSize: 16, opacity:0.5}}>An com chua</Text>
         </View>
         <View style={{flex: 1}}/>
         <Text>2 Ph√∫t</Text>
        </View>
      </View>
    </View>
  );
};

export default ChatList;
