import React from 'react';
import { View, Text, TouchableOpacity, TextInput, Image } from 'react-native';

const Message = () => {

  return (
    <View style={{flex: 1, backgroundColor: 'red'}}>
      {/* Nội dung màn hình */}
      <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
        <Text>Màn hình Message</Text>
      </View>

      {/* Phần dưới màn hình */}
      <View style={{flexDirection: 'row', justifyContent: 'space-around', alignItems: 'center', padding: 10}}>
        {/* Icon mặt cười */}
        <TouchableOpacity onPress={() => console.log('Pressed smiley')}>
          <Image source={require('path/to/smile-icon.png')} style={{width: 30, height: 30}} />
        </TouchableOpacity>

        {/* Ô nhập tin nhắn */}
        <TextInput
          style={{flex: 1, height: 40, borderColor: 'gray', borderWidth: 1, marginLeft: 10, marginRight: 10, borderRadius: 5, paddingLeft: 10}}
          placeholder="Nhập tin nhắn..."
        />

        {/* Icon menu */}
        <TouchableOpacity onPress={() => console.log('Pressed menu')}>
          <Image source={require('path/to/menu-icon.png')} style={{width: 30, height: 30}} />
        </TouchableOpacity>

        {/* Icon micro */}
        <TouchableOpacity onPress={() => console.log('Pressed microphone')}>
          <Image source={require('path/to/micro-icon.png')} style={{width: 30, height: 30}} />
        </TouchableOpacity>

        {/* Icon hình ảnh */}
        <TouchableOpacity onPress={() => console.log('Pressed image')}>
          <Image source={require('path/to/image-icon.png')} style={{width: 30, height: 30}} />
        </TouchableOpacity>
      </View>
    </View>
  );
};

export default Message;
