import { createContext, useContext, useState, useEffect } from "react";
import AsyncStorage from "@react-native-async-storage/async-storage";

const AuthContext = createContext();

export const useAuthContext = () => {
  return useContext(AuthContext);
};

AsyncStorage.clear();
export const AuthContextProvider = ({ children }) => {
  AsyncStorage.clear();
  const [authUser, setAuthUser] = useState(null);
  const [accessToken, setAccessToken] = useState(null);
  const [refreshToken, setRefreshToken] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        const AuthUser = await AsyncStorage.getItem("authUser");
        const AccessToken = await AsyncStorage.getItem("accessToken");
        const RefreshToken = await AsyncStorage.getItem("refreshToken");

        if (AuthUser) {
          setAuthUser(JSON.parse(AuthUser));
        }
        if (AccessToken) {
          setAccessToken(AccessToken);
        }
        if (RefreshToken) {
          setRefreshToken(RefreshToken);
        }
      } catch (error) {
        throw new Error("Error loading data from AsyncStorage:", error);
      }
    };

    loadData();
  }, []);
  useEffect(() => {
    const saveData = async () => {
      try {
        if (authUser) {
          // const encryptedAuthUser = base64Encode(); // Mã hóa base64
          await AsyncStorage.setItem("authUser", JSON.stringify(authUser));
        } else {
          await AsyncStorage.removeItem("authUser");
        }
        if (accessToken) {
          // const encryptedAccessToken = base64Encode();
          await AsyncStorage.setItem("accessToken", accessToken);
        } else {
          await AsyncStorage.removeItem("accessToken");
        }
        if (refreshToken) {
          // const encryptedRefreshToken = base64Encode(refreshToken);
          await AsyncStorage.setItem("refreshToken", refreshToken);
        } else {
          await AsyncStorage.removeItem("refreshToken");
        }
      } catch (error) {
        throw new Error("Error saving data to AsyncStorage:", error);
      }
    };

    saveData();
  }, [authUser]);

  return (
    <AuthContext.Provider
      value={{
        authUser,
        setAuthUser,
        accessToken,
        setAccessToken,
        refreshToken,
        setRefreshToken,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContextProvider;
