import React, { useState } from 'react';
import { View, Text, Pressable, Image, TextInput, Modal } from "react-native";
import { CheckBox } from 'react-native-elements';
import CountryDropdown from './CountryDropdown'


const CreateAccount1 = ({ navigation }) => {

    const [textPhone, setTextPhone] = useState();
    const [textPW, setTextPW] = useState();

    const handleTextChange = (input) => {
        const isValidInput = /^[0-9]{8,20}$/.test(input);
        setTextPhone(input);
        if (isValidInput) {
            setIsValidPhone(true);
        } else {
            setIsValidPhone(false);
        }
    };
    // const handlePressablePress = () => {
    //     if (isValidPhone && isCheckedInter && isCheckedUse) {
    //         toggleModal()
    //     }
    // };
    // const handleXacNhan = () => {

    //     toggleModal();
    //     navigation.navigate("EnterAuthCode");
    // };

    return (
        <View className={"flex-1  bg-white"}>
            <View className={"bg-gray-200"}><Text className={"font-bold text-black-400 p-2 text-center"}>Vui lòng nhập số điện thoại và mật khẩu để đăng nhập</Text></View>
            <View className={"flex flex-row m-5 "} >
                <TextInput
                    onChangeText={handleTextChange}
                    value={textPhone}
                    placeholder="Số điện thoại" className={"pl-3 font-semibold placeholder-gray-400 text-base border-b-gray-300 focus:border-b-[#64D6EA] border-b-[2px] focus:outline-none"}></TextInput>               
            </View>
            <View className={"flex flex-row m-5 border-b-gray-300 focus:border-b-[#64D6EA] border-b-[2px]"} >
            <TextInput
                    onChangeText={handleTextChange}
                    value={textPhone}
                    placeholder="Mật khẩu" className={"pl-3 font-semibold placeholder-gray-400 text-base "}></TextInput>

            </View>



        </View>
    )
};
export default CreateAccount1;
