import React from "react";
import {
  View,
  Text,
  Image,
  ImageBackground,
  Pressable,
  StyleSheet,
} from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";

const News = ({ navigation }) => {
  navigation.setOptions({
    headerRight: () => (
      <View style={styles.headerRightContainer}>
        <Pressable style={styles.headerIcon}>
          <Ionicons name="image" size={24} color="white" />
        </Pressable>
        <Pressable
          onPress={() => navigation.navigate("Notice")}
          style={styles.headerIcon}
        >
          <FontAwesomeIcons name="bell" size={24} color="white" />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View style={styles.headerTitleContainer}>
        <Ionicons
          name="search"
          size={24}
          color="white"
          style={styles.searchIcon}
        />
        <Text style={styles.headerTitleText}>Tìm kiếm</Text>
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },
  });

  return (
    <View>
      {/* Đăng status */}
      <View style={styles.statusContainer}>
        <View style={styles.avatarContainer}>
          <Image
            source={require("/assets/avata-story-3.png")}
            style={styles.avatar}
          />
          <Pressable
            style={styles.statusTextContainer}
            onPress={() => {
              navigation.navigate("PostStatus");
            }}
          >
            <Text style={styles.statusText}>Bạn đang nghĩ gì?</Text>
          </Pressable>
        </View>

        <View style={styles.buttonContainer}>
          <Pressable style={styles.button}>
            <Ionicons name="image" size={20} color="#56ce83" />
            <Text style={styles.buttonText}>Ảnh</Text>
          </Pressable>
          <Pressable style={styles.button}>
            <Ionicons name="videocam" size={20} color="#df34be" />
            <Text style={styles.buttonText}>Video</Text>
          </Pressable>
          <Pressable style={styles.button}>
            <Ionicons name="albums" size={20} color="#0a6bf4" />
            <Text style={styles.buttonText}>Album</Text>
          </Pressable>
          <Pressable style={styles.button}>
            <FontAwesomeIcons name="clock" size={20} color="#e67627" />
            <Text style={styles.buttonText}>Kỷ niệm</Text>
          </Pressable>
        </View>
      </View>

      {/* List story */}
      <View style={styles.storyContainer}>
        <Text style={styles.storyTitle}>Khoảng khắc</Text>
        <View style={styles.storyList}>
          <Pressable>
            <ImageBackground
              source={require("/assets/story_1.png")}
              style={styles.storyItem}
            >
              <Pressable style={styles.storyIconContainer}>
                <FontAwesomeIcons name="pen" size={10} color="white" />
              </Pressable>
              <Text style={styles.storyName}>Tạo mới</Text>
            </ImageBackground>
          </Pressable>

          {/* Add more story items here */}
        </View>
      </View>

      {/* List status */}
      <View style={styles.statusContainer}>
        <View style={styles.status}>
          <View style={styles.statusHeader}>
            <Pressable>
              <Image
                source={require("/assets/avata-story-1.png")}
                style={styles.statusAvatar}
              />
            </Pressable>
            <View style={styles.statusInfo}>
              <Pressable>
                <Text style={styles.statusName}>Thuỳ Linh</Text>
              </Pressable>
              <Text style={styles.statusTime}>9 phút trước</Text>
            </View>
            <Pressable style={styles.statusOptions}>
              <Image
                source={require("/assets/ic_threeDots.png")}
                style={styles.statusOptionsIcon}
                resizeMode="contain"
              />
            </Pressable>
          </View>
          <View style={styles.statusContent}>
            <Text style={styles.statusText}>
              “KHI BẠN CHỌN BÌNH YÊN, NÓ ĐI KÈM VỚI RẤT NHIỀU LỜI TẠM BIỆT”
            </Text>
          </View>
          <View style={styles.statusImageContainer}>
            <Image
              source={require("/assets/status-1.png")}
              style={styles.statusImage}
            />
          </View>
          <View style={styles.statusActions}>
            <View style={styles.statusLike}>
              <Pressable>
                <Ionicons name="heart" size={22} color="#ef243a" />
              </Pressable>
              <Text style={styles.statusLikeText}>
                Trần Hằng và 15 người khác
              </Text>
              <Text style={styles.statusCommentText}>1 bình luận</Text>
            </View>
            <View style={styles.statusButtons}>
              <Pressable style={styles.statusButton}>
                <Ionicons name="heart-outline" size={22} color="black" />
                <Text style={styles.statusButtonText}>Thích</Text>
              </Pressable>
              <Pressable style={styles.statusButton}>
                <Ionicons
                  name="chatbox-ellipses-outline"
                  size={22}
                  color="black"
                />
              </Pressable>
            </View>
          </View>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  headerRightContainer: {
    flexDirection: "row",
  },
  headerIcon: {
    padding: 5,
    marginLeft: 15,
  },
  headerTitleContainer: {
    flexDirection: "row",
    alignItems: "center",
  },
  searchIcon: {
    position: "absolute",
    marginLeft: 10,
  },
  headerTitleText: {
    color: "#71b9fe",
    fontSize: 18,
    fontWeight: "bold",
    marginLeft: 24,
  },
  statusContainer: {
    flexDirection: "row",
    backgroundColor: "white",
    padding: 10,
    alignItems: "center",
  },
  avatarContainer: {
    flexDirection: "row",
    alignItems: "center",
    width: "100%",
  },
  avatar: {
    margin: 10,
    width: 64,
    height: 64,
  },

  buttonContainer: {
    flexDirection: "row",
    backgroundColor: "white",
    padding: 10,
    justifyContent: "space-between",
  },
  button: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#f5f5f5",
    borderRadius: 25,
    // width: "22%",
    justifyContent: "center",
    paddingHorizontal: 15,
    paddingVertical: 8,
  },
  buttonText: {
    fontWeight: "600",
    marginLeft: 8,
  },
  storyContainer: {
    backgroundColor: "white",
    marginBottom: 10,
  },
  storyTitle: {
    fontWeight: 600,
    fontSize: 16,
    paddingHorizontal: 10,
    paddingTop: 10,
  },
  storyList: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingHorizontal: 10,
    paddingVertical: 10,
  },
  storyItem: {
    width: 90,
    height: 130,
    justifyContent: "flex-end",
    alignItems: "center",
  },
  storyIconContainer: {
    width: 30,
    height: 30,
    borderRadius: 15,
    backgroundColor: "#169dff",
    justifyContent: "center",
    alignItems: "center",
    marginBottom: 5,
  },
  storyName: {
    fontWeight: 600,
    color: "white",
    fontSize: 12,
  },
  statusContainer: {
    backgroundColor: "white",
    marginBottom: 10,
  },
  status: {
    paddingHorizontal: 10,
    paddingVertical: 10,
  },
  statusHeader: {
    flexDirection: "row",
    alignItems: "center",
  },
  statusAvatar: {
    width: 40,
    height: 40,
    borderRadius: 20,
    marginRight: 10,
  },
  statusInfo: {
    flex: 1,
  },
  statusName: {
    fontWeight: "bold",
  },
  statusTime: {
    fontSize: 12,
    color: "gray",
  },
  statusOptions: {
    width: 40,
    alignItems: "center",
  },
  statusOptionsIcon: {
    width: 15,
    height: 15,
  },
  statusContent: {
    marginTop: 10,
  },
  statusText: {
    color: "gray",
    fontSize: 18,
    fontWeight: 400,
  },
  statusImageContainer: {
    width: "100%",
    height: 200,
    marginTop: 10,
    justifyContent: "center",
    alignItems: "center",
  },
  statusImage: {
    width: "100%",
    height: "100%",
  },
  statusActions: {
    flexDirection: "row",
    marginTop: 10,
    alignItems: "center",
  },
  statusLike: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
  },
  statusLikeText: {
    marginLeft: 5,
    marginRight: 10,
  },
  statusCommentText: {
    marginRight: 10,
  },
  statusButtons: {
    flexDirection: "row",
  },
  statusButton: {
    flexDirection: "row",
    alignItems: "center",
    marginRight: 10,
  },
  statusButtonText: {
    marginLeft: 5,
  },
});

export default News;
