import React, { useEffect, useState } from "react";
import {
  Text,
  View,
  Image,
  ScrollView,
  Pressable,
  Modal,
  Switch,
} from "react-native";
import axiosInstance from "../../../api/axiosInstance";
import { Ionicons } from "@expo/vector-icons";
const FriendProfile = ({ navigation, route }) => {
  const { user } = route.params;
  const [isAnNhatKy, setAnNhatKy] = useState(false);
  const anNhatKy = () => setAnNhatKy(!isAnNhatKy);
  const [isChanXem, setChanXem] = useState(false);
  const chanXem = () => setChanXem(!isChanXem);
  const [isModalVisible, setModalVisible] = useState(false);
  
  const fetchFriend = async () => {
    try {
      const response = await axiosInstance.get(`/users/get/uid/${user._id}`);
      navigation.navigate("FriendProfileSettings", { user: response.data.user }  )
      
    } catch (error) {
      console.log(error);
    }
  };

  const PostItem = ({ post }) => (
    <View
      style={{
        marginHorizontal: 20,
        marginVertical: 10,
      }}
    >
      <Text style={{marginVertical: 10, fontWeight: "500"}}>{post.content}</Text>
      <View
        style={{
          flexDirection: "row",
          flexWrap: "wrap",
        }}
      >
        {post.image.map((imageUrl, index) => (
          <Image
            key={index}
            source={{ uri: imageUrl }}
            style={{
              width: 100,
              height: 100,
              paddingStart: 15,
              marginHorizontal: 5,
              marginVertical: 5,
            }}
          />
        ))}
      </View>

      <View
        style={{ flexDirection: "row", alignItems: "center", marginTop: 15, justifyContent: "space-between" }}
      >
        <View
          style={{ flexDirection: "row", alignItems: "center", marginRight: 5 }}
        >
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              marginRight: 5,
            }}
          >
            <Ionicons
              name="heart"
              size={20}
              color="red"
              style={{ padding: 8 }}
            />
            <Text>{post.likes} người</Text>
          </View>
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              marginRight: 5,
            }}
          >
            <Ionicons
              name="chatbox-ellipses-outline"
              size={20}
              color="black"
              style={{ padding: 8 }}
            />
            <Text style={{ marginEnd: 10 }}>{post.comments} bình luận</Text>
          </View>
        </View>
        <Ionicons
          name="ellipsis-horizontal-sharp"
          size={20}
          color="black"
          style={{ padding: 8 }}
        />
      </View>
    </View>
  );

  const DayItem = ({ day }) => (
    <View style={{ flex: 1 }}>
      <Text
        style={{
          fontSize: 16,
          fontWeight: "500",
          backgroundColor: "gray",
          opacity: 0.42,
          width: 150,
          marginLeft: 20,
          marginVertical: 10,
          textAlign: "center",
          borderRadius: 5,
        }}
      >
        {day.date}
      </Text>
      {day.posts.map((post, index) => (
        <PostItem key={index} post={post} />
      ))}
    </View>
  );

  useEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <View style={{ flexDirection: "row" }}>
          <Pressable>
            <Ionicons
              name="call-outline"
              size={24}
              color="white"
              style={{ padding: 10 }}
            />
          </Pressable>
          <Pressable onPress={() => setModalVisible(!isModalVisible)}>
            <Ionicons
              name="settings-outline"
              size={24}
              color="white"
              style={{ padding: 10 }}
            />
          </Pressable>
          <Pressable
            onPress={() => 
              {
                fetchFriend();
              }
              }
          >
            <Ionicons
              name="ellipsis-horizontal-sharp"
