import React from "react";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Ionicons } from "@expo/vector-icons";
import { View, Text, TextInput, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native";

import ChatList from "./ChatList";
import Contact from "./Contact";
import Explore from "./Explore";
import News from "./News";
import Info from "./Info";

const Tab = createBottomTabNavigator();

const ChatComponent = () => {
  const navigation = useNavigation();

  const handleSearchPress = () => {
    console.log("Search pressed");
    // Thực hiện các thao tác mong muốn khi icon search được nhấn
  };

  const handleQRPress = () => {
    console.log("QR pressed");
    // Thực hiện các thao tác mong muốn khi icon QR được nhấn
  };

  const handleAddFriendPress = () => {
    console.log("Add friend pressed");
    // Thực hiện các thao tác mong muốn khi icon add friend được nhấn
  };

  return (
    <Tab.Navigator
      initialRouteName="ChatList"
      screenOptions={({ route }) => ({
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;

          if (route.name === "ChatList") {
            iconName = focused
              ? "chatbubble-ellipses"
              : "chatbubble-ellipses-outline";
          } else if (route.name === "Contact") {
            iconName = focused ? "people" : "people-outline";
          } else if (route.name === "Explore") {
            iconName = focused ? "compass" : "compass-outline";
          } else if (route.name === "News") {
            iconName = focused ? "newspaper" : "newspaper-outline";
          } else if (route.name === "Info") {
            iconName = focused
              ? "information-circle"
              : "information-circle-outline";
          }
          return <Ionicons name={iconName} size={size} color={color} />;
        },
        headerTitle: () => (
          <View style={{ flex: 1, flexDirection: "row", alignItems: "center" }}>
            <TouchableOpacity onPress={handleSearchPress}>
              <Ionicons name="search" size={24} color="white" />
            </TouchableOpacity>
            <TextInput placeholder="Tìm kiếm" style={{ flex: 1, color: "white", marginLeft: 20, backgroundColor:'red', height:40, width:300}} />
          </View>
        ),
        headerRight: () => (
          <View style={{ flexDirection: "row", marginRight: 10, justifyContent: 'center', alignItems: 'center' }}>
            <TouchableOpacity onPress={handleQRPress}>
              <Ionicons name="qr-code" size={24} color="white" />
            </TouchableOpacity>
            <View style={{ marginLeft: 10 }}>
              <TouchableOpacity onPress={handleAddFriendPress}>
                <Ionicons name="add" size={35} color="white" />
              </TouchableOpacity>
            </View>
          </View>
        ),
        headerStyle: {
          backgroundColor: "#01A4F6", // Màu nền của header
        },
        headerTintColor: "white", // Màu chữ của header
      })}
    >
      <Tab.Screen name="ChatList" component={ChatList} />
      <Tab.Screen name="Contact" component={Contact} />
      <Tab.Screen name="Explore" component={Explore} />
      <Tab.Screen name="News" component={News} />
      <Tab.Screen name="Info" component={Info} />
    </Tab.Navigator>
  );
};

export default ChatComponent;
