import { View, Text, Pressable } from "react-native";
import React from "react";
import tailwind from "tailwindcss"

const Chat = () => {
  const isValidName = true;
  return (
    <View>
      <Pressable
        style={tailwind([
          "w-[70px] h-[70px] rounded-full flex justify-center items-center border ",
          isValidName ? " bg-green-400 " : " bg-red-400 ",
        ])}
      >
        <Text className="text-red-400">show chat content</Text>
      </Pressable>
    </View>
  );
};

export default Chat;
