import React from "react";
import {
  View,
  Text,
  Image,
  ImageBackground,
  ScrollView,
  Pressable,
  StyleSheet,
} from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";

const Notice = ({ navigation }) => {
  navigation.setOptions({
    headerRight: () => (
      <View style={{ flexDirection: "row" }}>
        <Pressable>
          <Ionicons
            name="settings-outline"
            size={24}
            color="white"
            style={{ padding: 5, paddingStart: 15 }}
          />
        </Pressable>
      </View>
    ),
    headerTitle: () => (
      <View style={{ flexDirection: "row", alignItems: "center" }}>
        <Text style={styles.headerTitle}>Thông báo mới</Text>
      </View>
    ),
    headerStyle: {
      backgroundColor: "#0091FF",
      shadowColor: "#fff",
    },
    headerTintColor: "#fff",
    headerTitleStyle: {
      fontWeight: "bold",
      fontSize: 20,
    },
  });

  return (
    <ScrollView contentContainerStyle={styles.container}>
      {/* Notice đăng story*/}
      <View style={styles.noticeContainer}>
        <Pressable style={styles.pressableContainer}>
          <ImageBackground
            source={require("/assets/avata-story-1.png")}
            style={styles.imageBackground}
            resizeMode="contain"
          >
            <View style={styles.iconContainer}>
              <FontAwesomeIcons name="video" size={14} color="white" />
            </View>
          </ImageBackground>
        </Pressable>
        <View style={styles.textContainer}>
          <Text style={styles.boldText}>
            Hoàng Thư{" "}
            <Text style={styles.mediumText}>đăng khoảng khắc mới </Text>
          </Text>
          <Text style={styles.grayText}>18:40 hôm qua</Text>
        </View>
        <Pressable style={styles.pressableContainer}>
          <Image
            source={require("/assets/ic_threeDots.png")}
            style={styles.imageIcon}
            resizeMode="contain"
          ></Image>
        </Pressable>
      </View>
      {/* Các notice khác */}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flexGrow: 1,
    backgroundColor: "white",
  },
  headerTitle: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
  },
  noticeContainer: {
    flexDirection: "row",
    alignItems: "center",
    height: 100,
    backgroundColor: "rgb(226,243,251)",
    marginVertical: 5,
    padding: 10,
  },
  pressableContainer: {
    width: "20%",
    height: "100%",
    justifyContent: "center",
    borderWidth: 1,
  },
  imageBackground: {
    width: "100%",
    height: "100%",
    justifyContent: "flex-end",
    alignItems: "flex-end",
  },
  iconContainer: {
    backgroundColor: "#9570d9",
    borderWidth: 2,
    borderColor: "white",
    borderRadius: 20,
    alignItems: "center",
    justifyContent: "center",
    width: 30,
    height: 30,
  },
  textContainer: {
    flex: 1,
    marginLeft: 10,
  },
  boldText: {
    fontWeight: "bold",
    fontSize: 16,
  },
  mediumText: {
    fontWeight: "normal",
  },
  grayText: {
    color: "#adcff4",
    fontWeight: "bold",
  },
  imageIcon: {
    width: 16,
    height: 16,
  },
});

export default Notice;
