import { View, Text, Pressable, Image, FlatList } from "react-native";
import React, { useState } from "react";

const ChatList = ({ navigation }) => {
  const [users, setUsers] = useState([
    {
      ten: 'Nguyen Van A',
      url: 'https://randomuser.me/api/portraits/men/68.jpg',
      tinNhan:'Hello',
      soTNChuaDoc: 6,
    },
    {
      ten: 'Luong thi Tho',
      url: 'https://randomuser.me/api/portraits/men/70.jpg',
      tinNhan:'Khoe khong',
      soTNChuaDoc: 0,
    },
    {
      ten: 'Nguyen Van Teo',
      url: 'https://randomuser.me/api/portraits/men/80.jpg',
      tinNhan:'An com chua',
      soTNChuaDoc: 0
    },
    {
      ten: 'Le Van Ty',
      url: 'https://randomuser.me/api/portraits/men/90.jpg',
      tinNhan:'Dang lam gi the',
      soTNChuaDoc: 5
    },
    {
      ten: 'Huynh Quoc Hao',
      url: 'https://randomuser.me/api/portraits/men/10.jpg',
      tinNhan:'may gio roi',
      soTNChuaDoc: 7
    },
    {
      ten: 'Bui Tri Thuc',
      url: 'https://randomuser.me/api/portraits/men/20.jpg',
      tinNhan:'lam bai tap chua',
      soTNChuaDoc: 3
    },
  ]);

  const renderItem = ({ item }) => {
    return (
      <Pressable
        onPress={() => {
          // Xử lý khi người dùng chọn một mục trong danh sách
          // Ví dụ: navigation.navigate('ChatDetail', { user: item });
        }}
      >
        <View style={{ height: 75, backgroundColor: "purple", flexDirection: "row", alignItems: 'center', marginHorizontal: 15 }}>
          <Image source={{ uri: item.url }} style={{ width: 50, height: 50, borderRadius: 25, marginEnd: 15 }} />
          <View>
            <Text style={{ fontSize: 18 }}>{item.ten}</Text>
            <Text style={{ fontSize: 16, opacity: 0.5 }}>{item.tinNhan}</Text>
          </View>
          <View style={{ flex: 1 }} />
          <View style={{ alignItems: 'center' }}>
            <Text style={{}}>2 Phút</Text>
            <Text style={{ backgroundColor: 'red', width: 20, height: 20, textAlign: 'center', borderRadius: 25 }}>{item.soTNChuaDoc}</Text>
          </View>
        </View>
      </Pressable>
    );
  };

  return (
    <View style={{ flex: 1 }}>
      <FlatList
        data={users}
        renderItem={renderItem}
        keyExtractor={(item, index) => index.toString()}
      />
    </View>
  );
};

export default ChatList;
