import React, { useEffect, useState } from "react";
import { View, Text, Image, Pressable, TextInput, ScrollView } from "react-native";
import Ionicons from "react-native-vector-icons/Ionicons";
import FontAwesomeIcons from "react-native-vector-icons/FontAwesome5";
import { useAuthContext } from "../../contexts/AuthContext";
import { format } from 'date-fns';


const PersonalInfo = ({ navigation }) => {
  const { authUser } = useAuthContext();
  const gender = authUser?.profile?.gender === "male" ? "nam" : "Nữ";

  const dob = authUser?.profile?.dob;
  let dobFormat = format(new Date(dob), 'dd/MM/yyyy')
  useEffect(() => {
    navigation.setOptions({
      headerRight: () => (
        <View style={{ flexDirection: "row", paddingHorizontal: 5 }}>
          <Pressable style={{ paddingHorizontal: 8 }}>
            <Ionicons name="sync-circle-outline" size={24} color="white" />
          </Pressable>
          <Pressable
            onPress={() => {
              navigation.navigate("PersonalDetail");
            }}
            style={{ paddingHorizontal: 8 }}
          >
            <Ionicons
              name="ellipsis-horizontal-sharp"
              size={24}
              color="white"
            />
          </Pressable>
        </View>
      ),
      headerTitle: () => (
        <Text style={{ color: "white", fontSize: 20, fontWeight: "bold" }}>
          Thông tin cá nhân
        </Text>
      ),

      headerTintColor: "#fff",
      headerStyle: {
        backgroundColor: "#0091FF",
        shadowColor: "#fff",
      },
    });
  }, [navigation]);

  return (
    <ScrollView style={{ backgroundColor: "#f1f2f6", flex: 1 }}>
      <View style={{}}>
        <Image
          source={{
            uri: authUser?.profile?.background?.url,
          }}
          style={{ width: "100%", height: 160 }}
        />
        <View style={{ display: "flex", flexDirection: 'row', marginTop: -100, alignItems: "left" }}>
          <Image
            source={{
              uri: authUser?.profile?.avatar?.url,
            }}
            style={{ width: 75, height: 75, borderRadius: 48 }}
          />
          <div style={{ display: "flex", flexDirection: 'column', justifyContent: 'center' }}><Text style={{ fontSize: 18, fontWeight: "bold", color: '#fff' }}>{authUser?.profile?.name}</Text></div>
        </View>
        <View>
          <Text>Thông tin cá nhân</Text>
          <div>
            <div style={{height:50, borderBottomWidth:1, borderBottomColor: '#bbb', display: "flex", flexDirection: 'row'}}>
              <Text style={{width:'30%',fontSize: 16, fontWeight: "500"}}>Giới tính</Text>
              <Text style={{fontSize: 16, fontWeight: "500"}}>{gender}</Text>
            </div>
            <div style={{ display: "flex", flexDirection: 'row'}}>
              <Text style={{width:'30%',fontSize: 16, fontWeight: "500"}}>Ngày sinh</Text>
              <Text style={{fontSize: 16, fontWeight: "500"}}>{dobFormat}</Text>
            </div>
            <div>
              <Text>Số điện thoại</Text>
              <Text>{authUser?.phone}</Text>
            </div>
          </div>
        </View>


      </View>
    </ScrollView>
  );
};

export default PersonalInfo;
