import React from 'react';

const countries = [
  'VN',
  'USA',
  'UK',
  'Cam',
];

const CountryDropdown = () => {
  return (
    <div className="relative inline-block text-left w-75 h-50">
      <select
        className=" px-2 py-2 leading-tight focus:outline-none focus:border-sky-300 text font-bold text-sky-400"
      >
        {countries.map((country, index) => (
          <option key={index} value={country} className='font-bold'>
            {country}
          </option>
        ))}
      </select>
     
    </div>
  );
};

export default CountryDropdown;
