import React from "react";
import {
  View,
  Text,
  Pressable,
  Image,
  FlatList,
  StyleSheet,
} from "react-native";

const listFriend = [
  "Báo mới",
  "Bộ y tế",
  "Điện máy xanh",
  "Game Center",
  "Thế giới di động",
  "Thời tiết",
];

const QA = ({ navigation }) => {
  const renderItem = ({ item }) => (
    <Pressable style={styles.officialAccountItem}>
      <Image style={styles.avatar} source={require("/assets/meomeo.jpg")} />
      <Text style={styles.officialAccountText}>{item}</Text>
    </Pressable>
  );

  return (
    <View style={styles.container}>
      <View style={styles.section}>
        <Pressable style={styles.item}>
          <Image
            style={styles.icon}
            resizeMode="contain"
            source={require("/assets/broadcast.png")}
          />
          <Text style={styles.itemText}>Tìm thêm Official Account</Text>
        </Pressable>
      </View>
      <View style={styles.section}>
        <Text style={styles.sectionText}>Official Account đã quan tâm</Text>
        <FlatList
          data={listFriend}
          renderItem={renderItem}
          keyExtractor={(item, index) => index.toString()}
          contentContainerStyle={styles.officialAccountsContainer}
          numColumns={2}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#7B8594",
  },
  section: {
    backgroundColor: "white",
    marginBottom: 10,
    padding: 10,
  },
  sectionText: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 10,
  },
  item: {
    flexDirection: "row",
    alignItems: "center",
  },
  itemText: {
    marginLeft: 10,
    fontWeight: "bold",
    fontSize: 16,
  },
  officialAccountsContainer: {
    justifyContent: "flex-start",
  },
  officialAccountItem: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 10,
    marginRight: 10,
  },
  avatar: {
    width: 60,
    height: 60,
    borderRadius: 30,
    marginRight: 10,
  },
  officialAccountText: {
    fontSize: 16,
    fontWeight: "bold",
  },
  icon: {
    width: 30,
    height: 30,
    marginRight: 10,
  },
});

export default QA;
