import React, { useState } from 'react';
import { Text, View, FlatList, Image, TextInput, ScrollView, Pressable, Modal,Switch } from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const PostItem = ({ post }) => (
  <View style={{ backgroundColor: 'white', marginHorizontal: 20, marginVertical: 10 }}>
    <Text>{post.content}</Text>
    <View style={{ flexDirection: 'row' }}>
      {post.image.map((imageUrl, index) => (
        <Image key={index} source={{ uri: imageUrl }} style={{ width: 100, height: 100, paddingStart: 15 }} />
      ))}
    </View>
    <View style={{ flexDirection: 'row', alignItems: 'center', marginTop: 10 }}>
      <Ionicons name="heart" size={20} color="red" style={{ padding: 8 }} />
      <Text>{post.likes} người</Text>
      <View style={{ flex: 1 }} />
      <Text style={{ marginEnd: 10 }}>{post.comments} bình luận</Text>
    </View>
    <View style={{ flexDirection: 'row', alignItems: 'center' }}>
      <Ionicons name="heart-outline" size={20} color="black" style={{ padding: 8 }} />
      <Ionicons name="chatbox-ellipses-outline" size={20} color="black" style={{ padding: 8 }} />
      <View style={{ flex: 1 }} />
      <Ionicons name="ellipsis-horizontal-sharp" size={20} color="black" style={{ padding: 8 }} />
    </View>
  </View>
);

const DayItem = ({ day }) => (
  <View>
    <Text style={{ fontSize: 16, fontWeight: 'bold', backgroundColor: '#545454', opacity: 0.42, width: 150, paddingStart: 20, marginVertical: 10, textAlign: 'center', borderRadius: 5 }}>{day.date}</Text>
    <FlatList
      data={day.posts}
      keyExtractor={(item) => item.content}
      renderItem={({ item }) => <PostItem post={item} />}
    />
  </View>
);

const Profile = () => {
  const [isĐanDauBanThan, setĐanhauBanThan] = useState(false);
  const danhDauBanThan = () => setĐanhauBanThan(previousState => !previousState)

  const [isModalVisible, setModalVisible] = useState(false)

  const showModal = () => {
    setModalVisible(true);
  }

  const hideModal = () => {
    setModalVisible(false);
  };

  const [postData, setPostData] = useState([
    {
      date: '2 tháng 2, 2024',
      posts: [
        {
          content: 'Nội dung bài đăng 2',
          image: [
            'https://randomuser.me/api/portraits/men/68.jpg',
            'https://randomuser.me/api/portraits/men/70.jpg',
            'https://randomuser.me/api/portraits/men/72.jpg',
          ],
          likes: 20,
          comments: 8,
        },
        {
          content: 'Nội dung bài đăng 5',
          image: [
            'https://randomuser.me/api/portraits/men/53.jpg',
          ],
          likes: 20,
          comments: 8,
        },
        // Các bài đăng khác của ngày này
      ],
    },
    {
      date: '1 tháng 2, 2024', // Định dạng ngày tháng năm
      posts: [
        {
          content: 'Nội dung bài đăng 1',
          image: [
            'https://randomuser.me/api/portraits/men/96.jpg',
            'https://randomuser.me/api/portraits/men/55.jpg',
          ],
          likes: 10,
          comments: 5,
        },
        // Các bài đăng khác của ngày này
      ],
    },
    // ...Thêm các ngày khác
  ]);

  return (
    <View style={{ flex: 1 }}>
      <View style={{ height: 62, flexDirection: 'row', alignItems: 'center', paddingHorizontal: 10, position: 'absolute', zIndex: 2 }}>
        <Pressable>
          <Ionicons name="arrow-back" size={24} color="white" style={{ padding: 10 }} />
        </Pressable>
        <View style={{ marginRight: 190 }} />
        <Pressable>
          <Ionicons name="call-outline" size={24} color="white" style={{ padding: 10 }} />
        </Pressable>
        <Pressable onPress={showModal}>
          <Ionicons name="settings-outline" size={24} color="white" style={{ padding: 10 }} />
        </Pressable>
        <Pressable>
          <Ionicons name="ellipsis-horizontal-sharp" size={24} color="white" style={{ padding: 10 }} />
        </Pressable>
      </View>
      <ScrollView>
        <View style={{ backgroundColor: '#F4F4F4' }}>
          <Image source={{ uri: 'https://randomuser.me/api/portraits/men/68.jpg' }} style={{ height: 200, resizeMode: 'cover' }} />
          <Image source={{ uri: 'https://randomuser.me/api/portraits/men/68.jpg' }} style={{ height: 150, width: 150, borderRadius: 75, borderWidth: 2, borderColor: 'white', marginTop: -90, alignSelf: 'center' }} />
          <View style={{ flexDirection: 'row', justifyContent: 'center' }}>
            <Text style={{ fontSize: 25, paddingStart: 45, fontWeight: '550' }}>Huynh Quoc Hao</Text>
            <Ionicons name="pencil" size={24} color="black" style={{ padding: 10 }} />
          </View>
          <View style={{ flexDirection: 'row', marginBottom: 20, justifyContent: 'space-between', alignItems: 'center', marginHorizontal: 20 }}>
            <Pressable style={{ width: 170, flexDirection: 'row', backgroundColor: 'white', alignItems: 'center', borderRadius: 15 }}>
              <Ionicons name="image" size={24} color="#005AE0" style={{ padding: 10 }} />
              <Text>Ảnh 3</Text>
            </Pressable>
            <Pressable style={{ width: 170, flexDirection: 'row', backgroundColor: 'white', alignItems: 'center', borderRadius: 15 }}>
              <Ionicons name="videocam" size={24} color="#0AE046" style={{ padding: 10 }} />
              <Text>Video</Text>
            </Pressable>
          </View>
          <View style={{ flex: 1 }}>
            <FlatList
              data={postData}
              keyExtractor={(item) => item.date}
              renderItem={({ item }) => <DayItem day={item} />}
            />
          </View>
        </View>
      </ScrollView>
      <Pressable
        style={{
          position: 'absolute',
          bottom: 16,
          right: 30,
          backgroundColor: 'white',
          flexDirection: 'row',
          alignItems: 'center',
          padding: 10,
          borderRadius: 50,
        }}
      >
        <Ionicons name="chatbubble-ellipses-outline" size={24} color="#0091FF" />
        <Text style={{ color: 'black', marginLeft: 8 }}>Nhắn tin</Text>
      </Pressable>
      <Modal
        animationType="none"
        transparent={true}
        visible={isModalVisible}
        onRequestClose={hideModal}
      >
        <Pressable style={{ flex: 1, justifyContent: 'flex-end' }} onPress={hideModal}>
          <View style={{ backgroundColor: 'white', padding: 10, borderTopStartRadius: 30 }}>
            <Text style={{textAlign:'center'}}>Cài đặt riêng tư</Text>
            <Pressable style={{ height: 50, paddingStart: 22, justifyContent: 'center' }}>
              <View style={{ flexDirection: 'row' }}>
                <Text style={{ fontSize: 18 }}>Đánh dấu bạn thân</Text>
                <View style={{ flex: 1 }} />

                <Switch
                  trackColor={{ false: '#767577', true: '#0091FF' }}
                  thumbColor={isĐanDauBanThan ? '#0091FF' : '#f4f3f4'}
                  onValueChange={danhDauBanThan}
                  value={isĐanDauBanThan}
                  style={{ marginEnd: 15 }}
                />
              </View>
            </Pressable>
            <Pressable style={{ height: 50, paddingStart: 22, justifyContent: 'center' }}>
              <View style={{ flexDirection: 'row' }}>
                <Text style={{ fontSize: 18 }}>Đánh dấu bạn thân</Text>
                <View style={{ flex: 1 }} />

                <Switch
                  trackColor={{ false: '#767577', true: '#0091FF' }}
                  thumbColor={isĐanDauBanThan ? '#0091FF' : '#f4f3f4'}
                  onValueChange={danhDauBanThan}
                  value={isĐanDauBanThan}
                  style={{ marginEnd: 15 }}
                />
              </View>
            </Pressable>
          </View>
        </Pressable>
      </Modal>
    </View>
  );
};



export default Profile;
